mapper.select:
SELECT COUNT(*)
FROM tb_item
WHERE 1=1

    AND item_name LIKE CONCAT('%', #{itemName}, '%')


    AND item_division IN

        #{division}



    AND item_expiration_hour >= #{minExpirationHour}


    AND item_expiration_hour <= #{maxExpirationHour}

SELECT item_seq AS itemSeq, item_name AS itemName, item_division AS itemDivision, item_expiration_hour AS itemExpirationHour, item_image_url AS itemImageUrl, item_price AS itemPrice, item_reg_date AS itemRegDate, item_mod_date AS itemModDate FROM tb_item WHERE 1=1
AND item_name LIKE CONCAT('%', #{itemName}, '%')
AND item_division IN
#{division}
AND item_expiration_hour >= #{minExpirationHour}
AND item_expiration_hour <= #{maxExpirationHour}
ORDER BY item_seq ASC LIMIT #{size} OFFSET #{offset}
