mapper.select:
SELECT DISTINCT pd.production_disbursement_seq , pd.work_order_seq , pd.production_disbursement_name , wo.warehouse_seq AS factory_seq , w1.warehouse_name AS factory_name , pd.production_disbursement_departure_date , pd.production_disbursement_status , ( SELECT GROUP_CONCAT(i.item_name SEPARATOR ", ") FROM tb_production_disbursement_item pdi JOIN tb_item i ON i.item_seq = pdi.item_seq WHERE pd.production_disbursement_seq = pdi.production_disbursement_seq ) AS item_name FROM tb_production_disbursement pd LEFT JOIN tb_production_disbursement_item pdi ON pd.production_disbursement_seq = pdi.production_disbursement_seq LEFT JOIN tb_work_order wo ON wo.work_order_seq = pd.work_order_seq JOIN tb_warehouse w1 ON wo.warehouse_seq = w1.warehouse_seq WHERE pd.production_disbursement_status NOT IN ('DELETE')
AND pd.production_disbursement_departure_date >= #{startDate}
AND pd.production_disbursement_departure_date <= #{endDate}
AND w1.warehouse_name LIKE CONCAT('%', #{factoryName}, '%')
AND pd.production_disbursement_status IN
#{status}
ORDER BY pd.production_disbursement_reg_date DESC , pd.production_disbursement_name LIMIT #{size} OFFSET #{offset}

SELECT COUNT(DISTINCT pd.production_disbursement_seq) FROM tb_production_disbursement pd LEFT JOIN tb_production_disbursement_item pdi ON pd.production_disbursement_seq = pdi.production_disbursement_seq LEFT JOIN tb_work_order wo ON wo.work_order_seq = pd.work_order_seq JOIN tb_warehouse w1 ON wo.warehouse_seq = w1.warehouse_seq
WHERE pd.production_disbursement_status NOT IN ('DELETE')
AND pd.production_disbursement_departure_date >= #{startDate}
AND pd.production_disbursement_departure_date <= #{endDate}
AND w1.warehouse_name LIKE CONCAT('%', #{factoryName}, '%')
AND pd.production_disbursement_status IN
#{status}

SELECT pd.production_disbursement_name , ( SELECT GROUP_CONCAT(i.item_name SEPARATOR ", ") FROM tb_production_disbursement_item pdi JOIN tb_item i ON i.item_seq = pdi.item_seq WHERE pd.production_disbursement_seq = pdi.production_disbursement_seq ) AS item_name , pdi.production_disbursement_quantity , ( SELECT GROUP_CONCAT(w.warehouse_name SEPARATOR ", ") FROM tb_production_disbursement_item pdi JOIN tb_warehouse w ON w.warehouse_seq = pdi.ingredients_warehouse_seq WHERE pd.production_disbursement_seq = pdi.production_disbursement_seq ) AS store_name , w1.warehouse_name AS factory_name , pd.production_disbursement_departure_date , pd.production_disbursement_status FROM tb_production_disbursement pd LEFT JOIN tb_production_disbursement_item pdi ON pd.production_disbursement_seq = pdi.production_disbursement_seq LEFT JOIN tb_work_order wo ON wo.work_order_seq = pd.work_order_seq LEFT JOIN tb_warehouse w1 ON wo.warehouse_seq = w1.warehouse_seq LEFT JOIN tb_warehouse w2 ON pdi.ingredients_warehouse_seq = w2.warehouse_seq WHERE pd.production_disbursement_status NOT IN ('DELETE')
AND pd.production_disbursement_departure_date >= #{startDate}
AND pd.production_disbursement_departure_date <= #{endDate}
AND w2.warehouse_name LIKE CONCAT('%', #{factoryName}, '%')
AND pd.production_disbursement_status IN
#{status}
ORDER BY pd.production_disbursement_reg_date DESC , pd.production_disbursement_name

SELECT DISTINCT pd.production_disbursement_name , pd.production_disbursement_departure_date , pd.production_disbursement_total_quantity , w1.warehouse_name AS factory_name , ( SELECT GROUP_CONCAT(w.warehouse_name SEPARATOR ", ") FROM tb_production_disbursement_item pdi JOIN tb_warehouse w ON w.warehouse_seq = pdi.ingredients_warehouse_seq WHERE pd.production_disbursement_seq = pdi.production_disbursement_seq ) AS store_name , pd.production_disbursement_note FROM tb_production_disbursement pd LEFT JOIN tb_production_disbursement_item pdi ON pd.production_disbursement_seq = pdi.production_disbursement_seq LEFT JOIN tb_work_order wo ON wo.work_order_seq = pd.work_order_seq LEFT JOIN tb_warehouse w1 ON wo.warehouse_seq = w1.warehouse_seq LEFT JOIN tb_warehouse w2 ON pdi.ingredients_warehouse_seq = w2.warehouse_seq WHERE pd.production_disbursement_status NOT IN ('DELETE')
AND pd.production_disbursement_departure_date >= #{startDate}
AND pd.production_disbursement_departure_date <= #{endDate}
AND w1.warehouse_name LIKE CONCAT('%', #{factoryName}, '%')
AND w2.warehouse_name LIKE CONCAT('%', #{storeName}, '%')
ORDER BY pd.production_disbursement_departure_date

SELECT DISTINCT pd.production_disbursement_name , pd.production_disbursement_departure_date , pd.production_disbursement_total_quantity , w1.warehouse_name AS factory_name , ( SELECT GROUP_CONCAT(w.warehouse_name SEPARATOR ", ") FROM tb_production_disbursement_item pdi JOIN tb_warehouse w ON w.warehouse_seq = pdi.ingredients_warehouse_seq WHERE pd.production_disbursement_seq = pdi.production_disbursement_seq ) AS store_name , pd.production_disbursement_note FROM tb_production_disbursement pd LEFT JOIN tb_production_disbursement_item pdi ON pd.production_disbursement_seq = pdi.production_disbursement_seq LEFT JOIN tb_work_order wo ON wo.work_order_seq = pd.work_order_seq LEFT JOIN tb_warehouse w1 ON wo.warehouse_seq = w1.warehouse_seq LEFT JOIN tb_warehouse w2 ON pdi.ingredients_warehouse_seq = w2.warehouse_seq WHERE pd.production_disbursement_status NOT IN ('DELETE')
AND pd.production_disbursement_departure_date >= #{startDate}
AND pd.production_disbursement_departure_date <= #{endDate}
AND w1.warehouse_name LIKE CONCAT('%', #{factoryName}, '%')
AND w2.warehouse_name LIKE CONCAT('%', #{storeName}, '%')
ORDER BY pd.production_disbursement_departure_date
