mapper.select:
SELECT tb_sales_order.sales_order_seq, tb_sales_order.sales_order_name, (SELECT GROUP_CONCAT(tb_item.item_name SEPARATOR ", ") FROM tb_sales_order_item JOIN tb_item USING (item_seq) WHERE tb_sales_order.sales_order_seq = tb_sales_order_item.sales_order_seq) AS item_name, tb_client.client_name, tb_sales_order.sales_order_order_date, tb_sales_order.sales_order_status FROM tb_sales_order JOIN tb_client USING (client_seq) WHERE TRUE
AND #{startDate} <= tb_sales_order.sales_order_order_date
AND #{endDate} >= tb_sales_order.sales_order_order_date
AND tb_client.client_name LIKE CONCAT('%', #{clientName}, '%')
AND tb_sales_order.sales_order_status LIKE #{salesOrderStatus}
LIMIT #{limit} OFFSET #{offset}
