mapper.select:
SELECT si.shipping_instruction_seq, si.shipping_instruction_name, si.shipping_instruction_status, si.shipping_instruction_scheduled_shipment_date, so.client_name, ( SELECT GROUP_CONCAT(i.item_name SEPARATOR ', ') FROM tb_shipping_instruction_item sii JOIN tb_item i ON sii.item_seq = i.item_seq WHERE sii.shipping_instruction_seq = si.shipping_instruction_seq ) AS item_names FROM tb_shipping_instruction si JOIN tb_sales_order so ON si.sales_order_seq = so.sales_order_seq WHERE si.shipping_instruction_status IN ('결재 전', '결재 후')
AND si.shipping_instruction_scheduled_shipment_date BETWEEN #{startDate} AND #{endDate}
AND so.client_name LIKE CONCAT('%', #{clientName}, '%')
AND si.shipping_instruction_status LIKE #{shippingInstructionStatus}
ORDER BY si.shipping_instruction_reg_date DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM tb_shipping_instruction si
JOIN tb_sales_order so ON si.sales_order_seq = so.sales_order_seq


        si.shipping_instruction_scheduled_shipment_date BETWEEN #{startDate} AND #{endDate}


        AND so.client_name LIKE CONCAT('%', #{clientName}, '%')


        AND si.shipping_instruction_status LIKE #{shippingInstructionStatus}
