mapper.select:
SELECT si.shipping_instruction_seq, si.shipping_instruction_name, si.shipping_instruction_status, si.shipping_instruction_scheduled_shipment_date, c.client_name, ( SELECT GROUP_CONCAT(i.item_name SEPARATOR ", ") FROM tb_shipping_instruction_item sii JOIN tb_item i ON i.item_seq = sii.item_seq WHERE si.shipping_instruction_seq = sii.shipping_instruction_seq ) AS item_name FROM tb_shipping_instruction si JOIN tb_sales_order so ON (si.sales_order_seq = so.sales_order_seq) JOIN tb_client c ON (so.client_seq = c.client_seq) WHERE si.shipping_instruction_status IN ('결재 전', '결재 후')
AND #{startDate} <= tb_quotation.quotation_quotation_date
AND #{endDate} >= tb_quotation.quotation_quotation_date
AND so.client_name LIKE CONCAT('%', #{clientName}, '%')
AND si.shipping_instruction_status LIKE #{shippingInstructionStatus}
LIMIT #{limit} OFFSET #{offset}
