mapper.select:
SELECT wo.work_order_seq , wo.work_order_name , w.warehouse_seq , w.warehouse_name , wo.work_order_indicated_date , wo.work_order_status , (SELECT GROUP_CONCAT(i.item_name SEPARATOR ', ') FROM tb_item i WHERE i.item_seq = wo.item_seq ) AS item_names FROM tb_work_order wo LEFT JOIN tb_warehouse w ON wo.warehouse_seq = w.warehouse_seq
work_order_status = #{workOrderStatus}
AND warehouse_name like concat('%', #{warehouseName}, '%')
AND work_order_indicated_date >= #{startDate}
AND work_order_indicated_date <= #{endDate}
ORDER BY wo.work_order_indicated_date DESC , wo.work_order_name LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM tb_work_order wo
LEFT JOIN tb_warehouse w ON wo.warehouse_seq = w.warehouse_seq


        work_order_status = #{workOrderStatus}


        AND warehouse_name like concat('%', #{warehouseName}, '%')


        AND work_order_indicated_date >= #{startDate}



        AND work_order_indicated_date <= #{endDate}
