SELECT wo.work_order_seq , wo.work_order_name , w.warehouse_seq , w.warehouse_name , wo.work_order_indicated_date , wo.work_order_status , (SELECT GROUP_CONCAT(i.item_name SEPARATOR ', ') FROM tb_item i WHERE i.item_seq = wo.item_seq ) AS item_name FROM tb_work_order wo LEFT JOIN tb_warehouse w ON wo.warehouse_seq = w.warehouse_seq
work_order_status = #{filter.workOrderStatus}
AND warehouse_name like concat('%', #{filter.warehouseName}, '%')
AND work_order_indicated_date >= #{filter.startDate}
AND work_order_indicated_date <= #{filter.endDate}
ORDER BY wo.work_order_indicated_date DESC , wo.work_order_name LIMIT #{filter.size} OFFSET #{offset}

SELECT COUNT(*)
FROM tb_work_order wo
LEFT JOIN tb_warehouse w ON wo.warehouse_seq = w.warehouse_seq


        work_order_status = #{filter.workOrderStatus}


        AND warehouse_name like concat('%', #{filter.warehouseName}, '%')


        AND work_order_indicated_date >= #{filter.startDate}



        AND work_order_indicated_date <= #{filter.endDate}

SELECT i.item_name , c.client_name , wo.work_order_seq , wo.work_order_name , w.warehouse_name , wo.work_order_indicated_quantity , wo.work_order_indicated_date , wo.work_order_due_date FROM tb_work_order wo LEFT JOIN tb_item i ON wo.item_seq = i.item_seq LEFT JOIN tb_client c ON wo.client_seq = c.client_seq LEFT JOIN tb_warehouse w ON w.warehouse_seq = wo.warehouse_seq
AND wo.work_order_indicated_date >= #{startDate}
AND wo.work_order_indicated_date <= #{endDate}
AND c.client_name LIKE CONCAT('%', #{clientName}, '%')
AND i.item_name LIKE CONCAT('%', #{warehouseName}, '%')
ORDER BY wo.work_order_indicated_date ASC
